/**
 * @title IStaking Interface
 * @dev Defines the external functions and events for the Staking contract.
 * This version supports a dynamic reward rate mechanism.
 */
interface IStaking {
    // --- Events ---
    event Staked(address indexed user, uint256 amount);
    event Unstaked(address indexed user, uint256 amount);
    event RewardClaimed(address indexed user, uint256 reward);

    /**
     * @dev Emitted when the reward rate is updated by the owner.
     */
    event RewardRateSet(uint256 newRate);

    /**
     * @dev Emitted when new tokens are added to the contract for funding rewards.
     */
    event RewardFunded(address indexed funder, uint256 amount);

    /**
     * @dev Kept for backward compatibility but is deprecated.
     */
    event RewardDistributed(uint256 totalReward);

    // --- Core User Functions ---
    function stake(uint256 amount) external;
    function unstake(uint256 amount) external;
    function claimReward() external;

    // --- View Functions ---
    function earned(address account) external view returns (uint256);
    function getStakedAmount(address user) external view returns (uint256);
    function totalSupply() external view returns (uint256);

    // --- Admin / Governance Functions ---

    /**
     * @notice Sets the rate of reward distribution.
     * @param _rewardRate The number of reward tokens distributed per second.
     */
    function setRewardRate(uint256 _rewardRate) external;

    /**
     * @notice Funds the contract with reward tokens.
     * @param amount The amount of tokens to add for rewards.
     */
    function fundRewards(uint256 amount) external;

    /**
     * @notice DEPRECATED: This function is kept for interface compatibility but will revert.
     * @dev Use `setRewardRate` and `fundRewards` for the new reward mechanism.
     */
    function distributeRewards(uint256 totalReward) external;
}

