        address recipient,
        uint256 totalAmount,
        uint256 releasedAmount,
        uint8 milestoneCount,
        uint8 currentMilestone,
        bool isActive
    );

    function platformFeeBps() external view returns (uint256);

    function daoAddress() external view returns (address);
}

